import { useEffect, useState } from "react"
import { Icon } from "semantic-ui-react"

function Number({ topDisplay, setTopDisplay,setTopResult ,defaults, setDefaults, firstN, secondN, handleResult, reCall }) {
    const [firstNum] = useState({ value: true })
    const [sperator] = useState({ value: '' })

    useEffect(()=>{
        if(sperator.value != '')
        clickNumber(sperator.value)
    },[reCall])

    const clickNumber = (e) => {
        let submitNum = e?.target?.innerText || e
            
        if (submitNum === '=') {
            handleResult(sperator.value)
            sperator.value = submitNum;
            secondN.value = ''
            firstNum.value = true;
            return;
        }
        if (!['+', '-', 'x', '/'].includes(submitNum)) {
            if (firstNum.value) {
                firstN.value = firstN.value + '' + submitNum
            } else {
                secondN.value = secondN.value + '' + submitNum
            }

            setTopResult(x => {
                let newNumber = x + '' + submitNum
                if (x === 0) {
                    newNumber = submitNum
                }
                return newNumber
            } )
        } else {
            setTopResult(0)
            if (!firstNum.value){
                handleResult(sperator.value,true)
                sperator.value = submitNum;
                secondN.value = ''
                firstNum.value = true;
                return;
            }
            sperator.value = submitNum
            firstNum.value = false

           
        }
        let newNumber = defaults + '' + submitNum
        if (defaults === 0) {
            newNumber = submitNum
        }
        setTopDisplay(newNumber)
        setDefaults(newNumber)
    }

    const handleDelete = () => {
        firstN.value = ''
        secondN.value = ''
        firstNum.value = true
        setDefaults(0)
        setTopDisplay()
        setTopResult(0)
    }

    const handleDeleteOneNumber = () => {

        if (topDisplay === 0) {
            return
        }

        let newNumber = topDisplay.slice(0, -1)
        if (topDisplay === '') {
            newNumber = 0
        }
        setTopDisplay(newNumber)
        setTopResult(newNumber)
    }
    const arrButton = [
        [
            { text: '%' },
            { text: 'CE' },
            { text: 'C', onClick: handleDelete },
            { text: <Icon name='window close'></Icon>, onClick: handleDeleteOneNumber },
        ],
        [
            { text: '1/x' },
            { text: <Icon name='superscript'></Icon>, },
            { text: '123', },
            { text: '/' },
        ],
        [
            { text: '7' },
            { text: '8' },
            { text: '9' },
            { text: 'x' },
        ],
        [
            { text: '4' },
            { text: '5' },
            { text: '6' },
            { text: '-' },
        ],
        [
            { text: '1' },
            { text: '2' },
            { text: '3' },
            { text: '+' },
        ],
        [
            { text: '+/-' },
            { text: '0' },
            { text: '.' },
            { text: '=' },
        ]
    ]

    return (
        <div>
            <table>
                <tbody>
                    {arrButton.map(tr =>
                        <tr>
                            {tr.map(td =>
                                <td className="td1" onClick={td.onClick ? td.onClick : clickNumber}>
                                    {td.text}
                                </td>)}
                        </tr>)}
                </tbody>
            </table>
        </div>
    )
}

export default Number
