import { useEffect, useState } from "react"
import { Icon } from "semantic-ui-react"

function Number({ currentNumber, setcurrentNumber, firstNumber, secondNumber ,handleResult, reCall, topDisplay, setTopDisplay, setTopResult}) {
    const [firstNum] = useState({ value: true })
    const [calculator] = useState({value:''})

    useEffect(()=> {
        if (calculator.value != '')
        clickNumber(calculator.value)
    },reCall)
    
    const clickNumber = (e) => {
        let submitNum = e.target.innerText
        
        if (submitNum === '=') {
            handleResult(calculator.value)
           
            calculator.value = submitNum;
            secondNumber.value = ''
            firstNum.value = true;
            return ;
        }
        if (!['+', '-', 'x', '/'].includes(submitNum)) {
            if (firstNum.value) {
                firstNumber.value = firstNumber.value + '' + submitNum
            } else {
                secondNumber.value = secondNumber.value  + '' + submitNum
            }
            setTopResult(x => {
                let newNumber = x + '' + submitNum
                if (x === 0) {
                    newNumber = submitNum
                }
            })
        }else{
            if(!firstNum.value) return
            calculator.value = submitNum
            firstNum.value = false
        }
        let newNumber = currentNumber + '' + submitNum
        if (currentNumber === 0) {
            newNumber = submitNum
        }
        setcurrentNumber(newNumber)
    }

    const handleDelete = () => {
        firstNumber.value = ''
        secondNumber.value = ''
        firstNum.value = true
        setcurrentNumber(0)
    }

    const handleDeleteOneNumber = () => {

        if (currentNumber === 0) {
            return
        }

        let newNumber = currentNumber.slice(0, -1)
        if (currentNumber === '') {
            newNumber = 0
        }
        setcurrentNumber(newNumber)
    }

    const arrButton = [
        [
            { text: '%'},
            { text: 'CE'},
            { text: 'C', onClick: handleDelete},
            { text: <Icon name='window close'></Icon>, onClick: handleDeleteOneNumber}
        ]
        [
            { text: '1/x'},
            { text: <Icon name='superscript'></Icon>},
            { text: '123'},
            { text: '/'}
        ]
        [
            { text: '7'},
            { text: '8'},
            { text: '9'},
            { text: 'x'}
        ]
        [
            { text: '4'},
            { text: '5'},
            { text: '6'},
            { text: '-'}
        ]
        [
            { text: '1'},
            { text: '2'},
            { text: '3'},
            { text: '+'}
        ]
        [
            { text: '+/-'},
            { text: '0'},
            { text: '.'},
            { text: '='}
        ]
    ]

    return (
        <div>
            <table>
                <tbody>
                    {arrButton.map(tr => 
                    <tr>
                        {tr.map(td => 
                        <td className="td1" onClick={td.onClick ? td.onClick : clickNumber}>
                            {td.text}
                        </td>)}
                    </tr>)}
                </tbody>
            </table>
        </div>
    )
}

export default Number
